<?php$route = '/page/:page_id/';$app->get($route, function ($page_id)  use ($app){	$host = $_SERVER['HTTP_HOST'];	$page_id = prepareIdIn($page_id,$host);	$ReturnObject = array();	$Query = "SELECT * FROM page WHERE page_id = '" . $page_id . "'";	$Query .= " ORDER BY title ASC";	//echo $Query . "<br />";	$PressResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Press = mysql_fetch_assoc($PressResult))		{		$page_id = $Press['page_id'];		$title = $Press['title'];		$body = $Press['body'];		// manipulation zone		$page_id = prepareIdOut($page_id,$host);				$F = array();		$F['page_id'] = $page_id;		$F['title'] = $title;		$F['body'] = $body;		$ReturnObject = $F;		}		$app->response()->header("Content-Type", "application/json");		echo format_json(json_encode($ReturnObject));	});?>